import path from "path";
import fs from "fs";
import datefns from "date-fns/utc";

import { makeEntry } from "../server/entires";

export default function Index({ entries }) {
  return (
    <div>
      {entries.map(({ html, date }, idx) => (
        <div key={idx}>
          <div>{datefns.format(new Date(date), "MMMM d, yyyy")}</div>
          <div dangerouslySetInnerHTML={{ __html: html }} />
        </div>
      ))}
    </div>
  );
}

async function mapArrayAsync(array, callback) {
  const resultingArray = [];
  for (let index = 0; index < array.length; index++) {
    resultingArray.push(await callback(array[index], index, array));
  }
  return resultingArray;
}

export async function getStaticProps() {
  const entriesDir = path.join(process.cwd(), "entries");
  console.log(entriesDir);
  const filepaths = await fs.promises.readdir(entriesDir);
  const entries = await mapArrayAsync(filepaths, (filepath) =>
    makeEntry(filepath, entriesDir)
  );

  return {
    props: { entries },
  };
}
